import React from "react";
import styled from "styled-components";

const MainSection = () => {
  return (
    <>
      <TopSection>
        <Intro className="container">
          <TextGroup>
            <Heading>Slack is where the future works</Heading>
            <Paragraph>
              Transform the way you work with one place for <br /> everyone and
              everything you need to get stuff done.
            </Paragraph>
            <ButtonGroup>
              <TryButton>Try For Free</TryButton>
            </ButtonGroup>
          </TextGroup>
          <Image src="./images/img-campaign-hero.jpg" />
        </Intro>
      </TopSection>
    </>
  );
};

export default MainSection;

const TopSection = styled.section`
  background-color: #f6efe8;
  height: 100vh;
`;

const Intro = styled.div`
  display: flex;
  justify-content: space-between;
  height: 500px;
  margin-top: 80px;
  padding: 60px 0 0 0;
`;

const TextGroup = styled.div`
  padding: 20px 0 0 0;
`;

const Paragraph = styled.p`
  font-size: 18px;
  margin-top: 10px;
  line-height: 1.6;
`;

const Heading = styled.span`
  font-size: 60px;
  font-weight: 700;
  font-family: Slack-Larsseit, "Helvetica Neue", Helvetica, "Segoe UI", Tahoma,
    Arial, sans-serif;
`;

const Image = styled.img`
  height: 345px;
`;

const ButtonGroup = styled.div`
display`;

const TryButton = styled.a`
  background-color: #611f69;
  padding: 0.75rem 1rem;
  color: white;
  border: 1px solid #611f69;
  margin: 0 10px;
  font-weight: 600;
  text-transform: uppercase;
  border-radius: 3px;
  font-size: 14px;
  cursor: pointer;
  

  &:hover {
    background-color: #4a154b;
  }
`;
